mapper.select:

select meet_up_id, title, person, category, intro
from (
     select /*+INDEX_ASC(meetup PK_MeetUp) */ rownum rn,
            meet_up_id, title, person, category, intro
     from meetup
     where rownum <= #{pageNum} * #{amount}
     )
where rn > (#{pageNum} -1) * #{amount}

select meet_up_id, title, person, category, intro from ( select /*+INDEX_ASC(meetup PK_MeetUp) */ rownum rn, meet_up_id, title, person, category, intro from meetup where
rownum <= #{cri.pageNum} * #{cri.amount}
and title like '%' || #{title} || '%'
) where
rn > (#{cri.pageNum} -1) * #{cri.amount}
