mapper.select:

select BOARD_ID as postId, TITLE as title, CONTENT as content, NICKNAME as nickname, CREATED_AT AS createdAt
from (
     select /*+LEADING(M B) INDEX_DESC(B PK_BOARD) */ rownum rn,
            B.BOARD_ID, B.TITLE, B.CONTENT, M.NICKNAME, B.CREATED_AT, B.MEMBER_ID as b_member_id
     from member M, board B
     where m.member_id = b.member_id and b.meet_up_id = #{meetUpId, jdbcType=BIGINT}
     and rownum <= #{cri.pageNum, jdbcType=BIGINT} * #{cri.amount, jdbcType=BIGINT}
     )
where rn > (#{cri.pageNum, jdbcType=BIGINT} -1) * #{cri.amount, jdbcType=BIGINT}
